function burgerOnClickHandler(e){const c=document.querySelector(".menu__btn"),s=document.querySelector("#idNavMenu"),t=document.body;c.classList.contains("menu-active")?(c.classList.remove("menu-active"),s.classList.remove("menu-active"),t.classList.remove("menu-active")):(c.classList.add("menu-active"),s.classList.add("menu-active"),t.classList.add("menu-active"))}