function burgerOnClickHandler(e){const c=document.querySelector(".menu__btn"),s=document.querySelector("#menu-list"),t=document.querySelector("#menu-background"),a=document.body;c.classList.contains("active")?(c.classList.remove("active"),s.classList.remove("active"),t.classList.remove("active"),a.classList.remove("active")):(c.classList.add("active"),s.classList.add("active"),t.classList.add("active"),a.classList.add("active"))}